import { AfterViewInit, Component, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Employee } from './models/employee.model';
import { EmployeeService } from './service/employee.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit, AfterViewInit {
  @ViewChild('fileInput') fileInput : any
  title = 'Employee';

  employeeForm: FormGroup;

  employees : Employee[] ;
  employeesToDisplay : Employee[]
  // firstname : any
  // lastname : any


  // firstname : any
  // lastname : any
  educationOptions = [
    '10th pass',
    'diploma',
    'garduation', 
    'post graduation',
    'Phd'
  ]

  constructor(private fb: FormBuilder ,private employeeService : EmployeeService ) {
    this.employeeForm = fb.group({})
    this.employees =[]
    this.employeesToDisplay = this.employees
  }

  // initVal() {
  //   this.firstname = ""
  //   this.lastname =""

  // }
  ngOnInit(): void {
    // this.initVal()
    this.employeeForm = this.fb.group({
      firstname: this.fb.control(''),
      lastname: this.fb.control(''),
      email : this.fb.control(''),
      birthday: this.fb.control(''),
      gender: this.fb.control(''),
      education: this.fb.control('default'),
      company: this.fb.control(''),
      jobExperience: this.fb.control(''),
      salary: this.fb.control('')

    });

    this.employeeService.getEmployees().subscribe(res =>{
      for(let emp of res){
        this.employees.unshift(emp)
      }
      this.employeesToDisplay = this.employees;
    })
  }
  ngAfterViewInit(): void {
    // this.buttontemp.nativeElement.click()
  }

  addEmployee(){
    let employee : Employee={
      firstname : this.FirstName.value,
      lastname : this.LastName.value,
      email : this.Email.value,
      birthday : this.Birthday.value,
      gender : this.Gender.value,
      education : this.educationOptions[parseInt(this.Education.value)],
      company: this.Company.value,
      jobExperience : this.JobExperience.value,
      salary : this.Salary.value,
      profile : this.fileInput.nativeElement.files[0]?.name,
    }

    this.employeeService.postEmployee(employee).subscribe((res)=>{
      this.employees?.unshift(res);
      this.clearForm();
    })
  }


  clearForm(){
    this.FirstName.setValue('');
    this.LastName.setValue('');
    this.Email.setValue("");
    this.Birthday.setValue('');
    this.Gender.setValue('');
    this.Education.setValue('');
    this.Company.setValue('');
    this.JobExperience.setValue('');
    this.Salary.setValue('');
    this.fileInput.nativeElement.value='';

  }

  public get FirstName(): FormControl{
    return this.employeeForm.get('firstname') as FormControl;
  }

  public get LastName(): FormControl{
    return this.employeeForm.get('lastname') as FormControl;
  }

  public get Birthday(): FormControl{
    return this.employeeForm.get('birthday') as FormControl;
  }
  public get Email(): FormControl{
    return this.employeeForm.get('email') as FormControl
  }
  public get Gender(): FormControl{
    return this.employeeForm.get('gender') as FormControl;
  }

  public get Education(): FormControl{
    return this.employeeForm.get('education') as FormControl;
  }

  public get Company(): FormControl{
    return this.employeeForm.get('company') as FormControl;
  }

  public get JobExperience(): FormControl{
    return this.employeeForm.get('jobExperience') as FormControl;
  }

  public get Salary(): FormControl{
    return this.employeeForm.get('salary') as FormControl;
  }
}
<div class="container-fluid g-0">
    <!-- header -->
    <app-header></app-header>

    <!-- main content -->

    <main >
        <!-- Toolbar to add and search employees -->
        <div class="toolbar">

            <div class="add-employee">
                <button type="button" class="btn btn-outline-success btn-lg"
                data-bs-toggle="modal" data-bs-target="#exampleModal" > Add Employees
            </button>
            </div>

            <div class="search-employees">
                <input type="search" class="form-control" placeholder="Search Employees">
            </div>
        </div>

        <!-- Area to display all the employees -->

        
        <div class="employees black-border" >
            <div class="row mt-5" *ngIf="employees.length == 0">
                <div class="col text-center">
                    <span class="text-secondary fs-2 fw-bold fst-italic">There are no Employees Added!</span>
                </div>
            </div>

            <div class="row mt-4" *ngFor="let employee of employeesToDisplay ; let ei = index;">
                <div class="col">
                    <app-employee></app-employee>
                </div>
            </div>
        </div>
    </main>
</div>



<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title" id="exampleModalLabel">Enter Employee Information</h5>
                <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
            </div>
            <div class="modal-body">
                <form [formGroup]="employeeForm">
                    <!-- First and Last Name -->
                    <div class="row" >
                        <div class="col" >
                            <input type="" class="form-control" placeholder="First Name" formControlName="firstname">
                            
                        </div>
                        <div class="col">
                            <input type="" class="form-control" placeholder="Last Name" formControlName="lastname">
                            
                        </div>
                    </div>

                    <div class="row mt-3">
                        <input type="" class="form-control" placeholder="E-mail" formControlName="email">
                        
                    </div>
                    <!-- Personal Information -->
                    <div class="row mt-5">
                        <div class="col form-heading">
                            <h6 class="text-secondary fst-italic">Personal Information</h6>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col">
                            <label for="datepicker" class="form-label">Birthdate</label>
                            <input type="date" class="form-control" id="datepicker" formControlName="birthday">
                        </div>
                        <div class="col">
                            <div class="row">
                                <h6 class="text-secondary">Gender</h6>
                            </div>
                            <div class="row">
                                <div class="col">
                                    <div class="form-check form-check-inline">
                                        <input class="form-check-input" type="radio" name="gender" id="male" value="m"
                                        formControlName="gender">
                                        <label class="form-check-label" for="male">Male</label>
                                    </div>
                                    <div class="form-check form-check-inline">
                                        <input class="form-check-input" type="radio" name="gender" id="female" value="f"
                                        formControlName="gender"
                                        >
                                        <label class="form-check-label" for="male">Female</label>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="row mt-3">
                        <div class="col">
                            <select class="form-select" name="" id="" formControlName="education">
                                <option value="default" selected>
                                    Select Your Education
                                </option>
                                <option *ngFor="let option of educationOptions; let i=index;" value="{{i}}">
                                    {{option | titlecase}}
                                </option>
                            </select>
                        </div>
                    </div>
                    <div class="row mt-3">
                        <div class="col">
                            <label for="profile" class="form-label">Profile Image</label>
                            <input type="file" class="form-control" id="profile" 
                            #fileInput>
                        </div>
                    </div>


                    <!-- job Information -->
                    <div class="row mt-3">
                        <div class="col form-heading">
                            <h6 class="text-secondary fst-italic">Job Information</h6>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col">
                            <input type="text" class="form-control" placeholder="Company Name" 
                            formControlName="company">
                        </div>
                    </div>
                    <div class="row mt-3">
                        <div class="col">
                            <input type="text" class="form-control" placeholder="Job Experience (yrs)"
                            formControlName="jobExperience">
                            
                        </div>

                        <div class="col">
                            <input type="number" class="form-control" placeholder="Salary(LPA)" formControlName="salary">
                        </div>
                    </div>
                </form>
            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-primary" data-bs-dismiss="modal" (click)="addEmployee()">Insert</button>
            </div>
        </div>
    </div>
</div>
