import { AfterViewInit, Component, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormArray, FormControl, FormGroup, Validators } from '@angular/forms';
import { Employee } from './models/employee.model';
import { EmployeeService } from './service/employee.service';
import  axios from 'axios';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit, AfterViewInit {
  @ViewChild('fileInput') fileInput: any
  @ViewChild('addEmployeeButton') addEmployeeButton: any;

  title = 'Employee';

  submitted = false;

  employeeForm: FormGroup;

  employees: Employee[];

  employeesToDisplay: Employee[];

  getUpdateId : any;

  // educationOptions = [
  //   '10th pass',
  //   'diploma',
  //   'graduation',
  //   'post graduation',
  //   'Phd'
  // ]

  showAdd : boolean = false;
  showUpdate : boolean = false;

  constructor(private fb: FormBuilder, private employeeService: EmployeeService) {
    this.employeeForm = fb.group({})
    this.employees = []
    this.employeesToDisplay = this.employees

  }

  ngOnInit(): void {

    this.employeeForm = this.fb.group({

      firstname: ['', Validators.required],
      lastname: ['', Validators.required],
      email: ['', [Validators.required , Validators.email]],
      mobile : ['',Validators.required],
      // birthday: ['', Validators.required],
      // gender: ['', Validators.required],
      // education: ['', Validators.required],
      // company: ['', Validators.required],
      jobExperience: ['', Validators.required],
      salary: ['', Validators.required],
      // profile:['',Validators.required]
    });
    this.getEmployeeData();
  }

  ngAfterViewInit(): void {

  }
  //try
  emp :any;

  getEmployeeData(){
    axios.get('http://localhost:3000/api/v1/employee').then((Response: { data: any; })=>{
      console.log(Response.data)

      //try
      this.emp = Response.data;

    })

    // this.employeeService.getEmployees().subscribe((res) => {
    //   for (let emp of res) {
    //     this.employees.unshift(emp)
    //   }
    //   this.employeesToDisplay = this.employees;
    // });
  }

  addEmployee() {
    console.log(this.employeeForm.valid);


    if (this.employeeForm.valid) {
      let employee: Employee = {

        firstname: this.FirstName.value,
        lastname: this.LastName.value,
        email: this.Email.value,
        mobile : this.Mobile.value,
        // birthday: this.Birthday.value,
        // gender: this.Gender.value,
        // education: this.educationOptions[parseInt(this.Education.value)],
        // company: this.Company.value,
        jobExperience: this.JobExperience.value,
        salary: this.Salary.value,
        // profile: this.fileInput.nativeElement.files[0]?.name,
      }

      this.employeeService.postEmployee(employee).subscribe((res) => {
        this.employees?.unshift(res);
        this.clearForm();
      })
    }
    else {
      alert("form is not valid");
      this.clearForm();
    }

  }

  removeEmployee(event: any) {
    this.employees.forEach((val, index) => {
      if (val.id === parseInt(event)) {
        this.employeeService.deleteEmployee(event).subscribe((res) => {
          this.employees.splice(index, 1);
        });
      }
    });
  }

  clickAddEmployee(){
    this.clearForm();
    this.showAdd = true
    this.showUpdate = false
  }


  editEmployee(event: any) {

    this.showAdd = false;
    this.showUpdate  = true;

    console.log(this.showAdd, this.showUpdate);

    this.employees.forEach((val, ind) => {
      if (val.id === event) {
        // console.log(val);
        this.getUpdateId = val.id;
        this.setForm(val);
      }


    });


  }

  updateEmployee(){

    console.log("this is update button click")

    if (this.employeeForm.valid) {
      let updateEmployee: Employee = {

        firstname: this.FirstName.value,
        lastname: this.LastName.value,
        email: this.Email.value,
        mobile :this.Mobile.value,
        // birthday: this.Birthday.value,
        // gender: this.Gender.value,
        // education: this.educationOptions[parseInt(this.Education.value)],
        // company: this.Company.value,
        jobExperience: this.JobExperience.value,
        salary: this.Salary.value,
        // profile: this.fileInput.nativeElement.files[0]?.name,
      }

      this.employeeService.updateEmployee(this.getUpdateId, updateEmployee).subscribe(


        res => {console.log("employee data is updated", res)},
        error =>{console.error("error in updating epmployee", error)}


      )
      location.reload();

    }
    else {
      alert('Some input fields are empty');
      this.clearForm();
    }



  }



  setForm(emp: Employee) {
    this.FirstName.setValue(emp.firstname);
    this.LastName.setValue(emp.lastname);
    // this.Birthday.setValue(emp.birthday);
    this.Email.setValue(emp.email);
    this.Mobile.setValue(emp.mobile);
    // this.Gender.setValue(emp.gender);

    // let educationIndex = 0;
    // this.educationOptions.forEach((val, index) => {
    //   if (val === emp.education) educationIndex = index;
    // });
    // this.Education.setValue(educationIndex);

    // this.Company.setValue(emp.company)
    this.JobExperience.setValue(emp.jobExperience);
    this.Salary.setValue(emp.salary);
    // this.fileInput.nativeElement = '';
  }


  searchEmployees(event : any){
    let filteredEmployees : Employee[] = [];

    if(event === ''){
      this.employeesToDisplay = this.employees;

    }
    else{
      filteredEmployees = this.employees.filter((val, index)=>{
        let targetKey = val.firstname.toLowerCase() + '' + val.lastname.toLowerCase();
        let searchKey = event.toLowerCase();

        return targetKey.includes(searchKey);
      });
      this.employeesToDisplay = filteredEmployees
    }
  }


  get myForm() {
    return this.employeeForm.controls;
  }



  clearForm() {
    this.FirstName.setValue('');
    this.LastName.setValue('');
    this.Email.setValue('');
    // this.Birthday.setValue('');
    // this.Gender.setValue('');
    // this.Education.setValue('');
    // this.Company.setValue('');
    this.JobExperience.setValue('');
    this.Salary.setValue('');
    // this.fileInput.nativeElement.value = '';

  }

  public get FirstName(): FormControl {
    return this.employeeForm.get('firstname') as FormControl;
  }

  public get LastName(): FormControl {
    return this.employeeForm.get('lastname') as FormControl;
  }
  public get Email(): FormControl {
    return this.employeeForm.get('email') as FormControl
  }

  public get Mobile(): FormControl {
    return this.employeeForm.get('mobile') as FormControl
  }
  // public get Birthday(): FormControl {
  //   return this.employeeForm.get('birthday') as FormControl;
  // }

  // public get Gender(): FormControl {
  //   return this.employeeForm.get('gender') as FormControl;
  // }

  // public get Education(): FormControl {
  //   return this.employeeForm.get('education') as FormControl;
  // }

  // public get Company(): FormControl {
  //   return this.employeeForm.get('company') as FormControl;
  // }

  public get JobExperience(): FormControl {
    return this.employeeForm.get('jobExperience') as FormControl;
  }

  public get Salary(): FormControl {
    return this.employeeForm.get('salary') as FormControl;
  }
}
