  ngOnInit(): void {
    console.log(this.employee)
    console.log("hello");

    // this.getEmployeeData();
  }

  deleteEmployeeClicked(){
    this.onRemoveEmployee.emit(this.employee.employeeid);
  }

  editEmployeeClicked(){
    this.onEditEmployee.emit(this.employee.employeeid, );

  }

