import { AfterViewInit, Component, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormArray, FormControl, FormGroup, Validators } from '@angular/forms';
import { Employee, postEmployee } from './models/employee.model';
import { EmployeeService } from './service/employee.service';
import  axios from 'axios';
import { EmployeeComponent } from './employee/employee.component';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit, AfterViewInit {
  @ViewChild('fileInput') fileInput: any
  @ViewChild('addEmployeeButton') addEmployeeButton: any;
  @ViewChild('EmployeeComponent') x !: EmployeeComponent


  title = 'Employee';

  submitted = false;

  employeeForm: FormGroup;

  employees: Employee[] = [];

  employeesToDisplay: Employee[] = [];

  allEmployees : Employee[] = [];

  getUpdateId : any;



  //for pagination
  // data : Employee[];
  // totalData : number = 0;
  // totalPages : number = 0;
  // itemsPerPage : number = 50;
  // pageNumber : number = 1;
  // startIndex : number = 0;
  // endIndex : number = 0;
  // dataPerPage: Employee[];


  currentPage = 1;
  pageSize =  50;
  totalCount = 0;
  totalPages = 0;

  showAdd : boolean = false;
  showUpdate : boolean = false;

  constructor(private fb: FormBuilder, private employeeService: EmployeeService) {
    this.employeeForm = fb.group({})
    this.employees = []
    this.employeesToDisplay = []

    // this.data =  this.employees
    // this.dataPerPage = this.employees
    // this.getEmployeeData = this.getEmployeeData;
  }

  ngOnInit(): void {

    this.employeeForm = this.fb.group({
    //  employeeid: ['',Validators.required],
      first_name: ['', Validators.required],
      last_name: ['', Validators.required],
      email: ['', [Validators.required , Validators.email]],
      mobile_number : ['',Validators.required],
      experience: ['', Validators.required],
      salary: ['', Validators.required],

    });
    this.getEmployeeData();
    // this.paginatedData();
  }

  ngAfterViewInit(): void {
    // console.log(this.x);
  }


  // emp :any;

   getEmployeeData(){
    // axios.get(`http://localhost:3000/api/v1/employee?page=${this.currentPage}&pageSize=${this.pageSize}`).then((Response: { data: any; }) => {
      this.employeeService.getEmployees(this.currentPage, this.pageSize).subscribe((Response : any )=>{
      // console.log(Response)
      // console.log("app component")

       this.employees = Response.data;

       this.allEmployees = Response.data;

       this.totalCount = Response.totalCount;
       this.totalPages = Math.ceil(this.totalCount / this.pageSize);

       this.employeesToDisplay = this.employees;
    });
  }

  previousPage(){
    if(this.currentPage > 1){
      this.currentPage--;
      this.getEmployeeData();
    }
  }

  nextPage(){
